package ch02.assignmentOperator10;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 0;
		num = num + 10;
		System.out.println(num);
		num = num + 20;
		System.out.println(num);

		num += 30;
		System.out.println(num);
		num += 40;
		System.out.println(num);
	}

}
