package ch02.operator02;

public class ArithOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num0 = 10;
		int num1 = 3;

		System.out.println(num0 + num1);
		System.out.println(num0 - num1);
		System.out.println(num0 * num1);
		System.out.println(num0 / num1);
		System.out.println(num0 % num1);
		System.out.println();
	}

}
